import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import HeaderLogin from './components/headerLogin/HeaderLogin';
import 'bootstrap/dist/css/bootstrap.min.css';
import Login from './components/auth/Login';
import Menu from './components/menu/Menu';
import Home from './pages/home/Home';  // Sua página Home
import BankLayouts from './components/BankLayouts'; // Ajuste o caminho conforme a localização do seu arquivo



// Função para verificar se o usuário está autenticado
function isAuthenticated() {
    const token = localStorage.getItem('token');
    return token !== null;
}

// Rota protegida, redireciona se o usuário não estiver autenticado
function PrivateRoute({ children }) {
    return isAuthenticated() ? children : <Navigate to="/" />;
}

// Layout principal que exibe o Menu e a área de conteúdo (área em verde)
function Layout({ children }) {
    return (
        <div style={{ display: 'flex' }}>
            <Menu /> {/* Menu fixo na esquerda */}
            <div style={{ flex: 1, padding: '20px' }}> {/* Área do conteúdo principal */}
                {children}
            </div>
        </div>
    );
}

function App() {
    return (
        <Routes>
    <Route path="/" element={<Login />} />
    <Route 
        path="/menu" 
        element={
            <PrivateRoute>
                <Layout>
                    <Home /> {/* Tela padrão que aparece quando o usuário entra no menu */}
                </Layout>
            </PrivateRoute>
        } 
    />
    <Route 
        path="/home" 
        element={
            <PrivateRoute>
                <Layout>
                    <Home />
                </Layout>
            </PrivateRoute>
        }
    />
    <Route 
        path="/layouts" 
        element={
            <PrivateRoute>
                <Layout>
                    <BankLayouts /> {/* Componente que mostra os layouts */}
                </Layout>
            </PrivateRoute>
        }
    />
</Routes>

    );
}

export default App;
