import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../../components/headerLogin/HeaderLogin'; // Importação correta do HeaderLogin
import '../../components/auth/Login'; // Importação correta do Login
import '../../components/menu/Menu'; // Importação correta do Menu
import '../home/Home'; // Importação correta do Home
import './Layouts.css'; // Este está correto conforme sua estrutura de diretório

const BankLayouts = () => {
    const [layouts, setLayouts] = useState([]);
    const [selectedLayout, setSelectedLayout] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        fetch('http://banco.brasdesco/sistemas/banco/layout/300')
            .then(response => response.json())
            .then(data => setLayouts(data))
            .catch(error => console.error('Erro na busca de dados:', error));
    }, []);

    const handleLayoutClick = (layoutId) => {
        fetch(`http://banco.brasdesco/sistemas/banco/layout/details/${layoutId}`)
            .then(response => response.json())
            .then(data => {
                setSelectedLayout(data);
                navigate(`/layouts/${layoutId}`);
            })
            .catch(error => console.error('Erro ao buscar detalhes do layout:', error));
    };

    return (
        <div className="bank-layouts">
            <ul>
                {layouts.map(layout => (
                    <li key={layout.id} onClick={() => handleLayoutClick(layout.id)}>
                        <div className="bank-info">
                            <span>{layout.nome}</span>
                            <span>{layout.tipo}</span>
                            <Link to={`/layouts/${layout.id}`}>Detalhes</Link>
                        </div>
                    </li>
                ))}
            </ul>
            {selectedLayout && (
                <div>
                    <h2>Detalhes do Layout: {selectedLayout.nome}</h2>
                    <p>{selectedLayout.descricao}</p>
                </div>
            )}
        </div>
    );
};

export default BankLayouts;
