import React, { useState } from 'react';
import './Menu.css'; // Seu estilo customizado
import { FaChevronDown, FaChevronRight, FaHome, FaBook, FaUserGraduate } from 'react-icons/fa'; // Ícones
import { Offcanvas, Button } from 'react-bootstrap'; // Componentes do Bootstrap

function SidebarMenu() {
    const [show, setShow] = useState(false); // Estado para controlar a visibilidade do offcanvas
    const [openSubItem, setOpenSubItem] = useState(null); // Estado para sub-itens abertos

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    // Função para alternar a visibilidade dos sub-itens
    const toggleSubItem = (index) => {
        setOpenSubItem(openSubItem === index ? null : index);
    };

    return (
        <>
            {/* Botão que abre o menu lateral */}
            <Button variant="primary" onClick={handleShow} className="me-2">
                Abrir Menu Lateral
            </Button>

            {/* Offcanvas para o menu lateral */}
            <Offcanvas show={show} onHide={handleClose} placement="start">
                <Offcanvas.Header closeButton>
                    <Offcanvas.Title>Menu Lateral</Offcanvas.Title>
                </Offcanvas.Header>
                <Offcanvas.Body>
                    <ul className="menu-items list-unstyled">
                        <li className={openSubItem === 1 ? 'open' : ''} onClick={() => toggleSubItem(1)}>
                            <FaHome /> <span className="ms-2">Item 1</span>
                            {openSubItem === 1 ? <FaChevronDown className="float-end" /> : <FaChevronRight className="float-end" />}
                            {openSubItem === 1 && (
                                <ul className="submenu list-unstyled ps-3">
                                    <li>Sub-item 1.1</li>
                                    <li>Sub-item 1.2</li>
                                    <li>Sub-item 1.3</li>
                                </ul>
                            )}
                        </li>
                        <li className={openSubItem === 2 ? 'open' : ''} onClick={() => toggleSubItem(2)}>
                            <FaBook /> <span className="ms-2">Item 2</span>
                            {openSubItem === 2 ? <FaChevronDown className="float-end" /> : <FaChevronRight className="float-end" />}
                            {openSubItem === 2 && (
                                <ul className="submenu list-unstyled ps-3">
                                    <li>Sub-item 2.1</li>
                                    <li>Sub-item 2.2</li>
                                </ul>
                            )}
                        </li>
                        <li className={openSubItem === 3 ? 'open' : ''} onClick={() => toggleSubItem(3)}>
                            <FaUserGraduate /> <span className="ms-2">Item 3</span>
                            {openSubItem === 3 ? <FaChevronDown className="float-end" /> : <FaChevronRight className="float-end" />}
                            {openSubItem === 3 && (
                                <ul className="submenu list-unstyled ps-3">
                                    <li>Sub-item 3.1</li>
                                    <li>Sub-item 3.2</li>
                                </ul>
                            )}
                        </li>
                    </ul>
                </Offcanvas.Body>
            </Offcanvas>
        </>
    );
}

export default SidebarMenu;
