import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Usando useNavigate em vez de useHistory
import './Menu.css';
import { FaHome, FaBook, FaBars, FaUserCircle, FaUserPlus, FaSignOutAlt } from 'react-icons/fa';

const Menu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [username, setUsername] = useState('Guest');
  const [permission, setPermission] = useState(0);
  const [isLoading, setIsLoading] = useState(false); // Novo estado para controle do carregamento
  const navigate = useNavigate();

  useEffect(() => {
    const storedUsername = localStorage.getItem('username');
    const storedPermission = localStorage.getItem('permission');

    if (storedUsername && storedUsername !== 'undefined') {
      setUsername(storedUsername);
    }

    if (storedPermission) {
      const perm = parseInt(storedPermission, 10);
      setPermission(perm);
    }
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleLogout = () => {
    // Ativa o carregamento
    setIsLoading(true);

    // Simula um pequeno atraso antes de redirecionar
    setTimeout(() => {
      // Remove username e permission do localStorage
      localStorage.removeItem('username');
      localStorage.removeItem('permission');

      // Redireciona o usuário para a página de login
      navigate('/');

      // Desativa o carregamento após o redirecionamento
      setIsLoading(false);
    }, 1500); // Simula 1.5s de carregamento
  };

  return (
    <div>
      {isLoading ? ( // Verifica se está carregando
        <div className="loading-screen">
          <p>Carregando...</p>
        </div>
      ) : (
        <>
          <button className="hamburger" onClick={toggleMenu}>
            <FaBars />
          </button>

          <div className={`menu-container ${isMenuOpen ? 'open' : ''}`}>
            <div className="user-info">
              <FaUserCircle style={{ marginRight: '10px', fontSize: '24px' }} />
              <span>{username}</span>
            </div>

            <ul className="menu">
              <li>
                <FaHome />
                <Link to="/home">Home</Link>
              </li>
              <li>
                <FaBook />
                <Link to="/layouts">Layouts</Link>
              </li>
              {permission === 1 && (
                <li>
                  <FaUserPlus />
                  <Link to="/add-user">Add User</Link>
                </li>
              )}
            </ul>

            <ul className="menu-bottom">
              <li>
                <FaSignOutAlt />
                <button className="logout-button" onClick={handleLogout}>
                  Sair
                </button>
              </li>
            </ul>
          </div>

          {isMenuOpen && <div className="overlay" onClick={toggleMenu}></div>}
        </>
      )}
    </div>
  );
};

export default Menu;
