import React, { useState } from 'react';
import './Menu.css'; // Importa o CSS criado
import { FaHome, FaBook, FaGraduationCap, FaCalendarAlt, FaComments, FaQuestionCircle, FaBars, FaAngleDown } from 'react-icons/fa';

const Menu = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [isSubmenuOpen, setIsSubmenuOpen] = useState(false);
  
    const toggleMenu = () => {
      setIsMenuOpen(!isMenuOpen);
    };
  
    const toggleSubmenu = () => {
      setIsSubmenuOpen(!isSubmenuOpen);
    };
  
    return (
      <div>
        {/* Botão hambúrguer */}
        <HamburgerButton onClick={toggleMenu}>
          <FaBars />
        </HamburgerButton>
  
        {/* Menu lateral */}
        <MenuContainer isMenuOpen={isMenuOpen}>
          <MenuList>
            <MenuItem>
              <FaHome />
              <span>Home</span>
            </MenuItem>
            <MenuItem onClick={toggleSubmenu}>
              <FaBook />
              <span>Catálogo</span>
              <SubmenuIcon isSubmenuOpen={isSubmenuOpen} />
              {/* Submenu */}
              <Submenu isSubmenuOpen={isSubmenuOpen}>
                <SubmenuItem>JavaScript</SubmenuItem>
                <SubmenuItem>Node.js</SubmenuItem>
                <SubmenuItem>React</SubmenuItem>
              </Submenu>
            </MenuItem>
            <MenuItem>
              <FaGraduationCap />
              <span>Pós-Graduação</span>
            </MenuItem>
            <MenuItem>
              <FaCalendarAlt />
              <span>Eventos</span>
            </MenuItem>
            <MenuItem>
              <FaComments />
              <span>Fórum</span>
            </MenuItem>
          </MenuList>
  
          {/* Itens do menu inferior */}
          <MenuList>
            <MenuItem>
              <FaQuestionCircle />
              <span>Ajuda</span>
            </MenuItem>
          </MenuList>
        </MenuContainer>
      </div>
    );
  };
  
  export default Menu;
