import '../../components/headerLogin/HeaderLogin'; 
import '../../components/auth/Login'; 
import '../../components/menu/Menu'; 
import '../home/Home'; 
import React, { useState, useEffect } from 'react'; 
import './CreateUser.css'; 
import { createUser, fetchUsers, deleteUser } from '../../services/userService'; 
import { toast, ToastContainer } from 'react-toastify';  
import 'react-toastify/dist/ReactToastify.css';  
import { ClipLoader } from 'react-spinners'; 

function CreateUserComponent() {
    const [user, setUser] = useState({
        nome: '',
        email: '',
        password: '',
        confirmPassword: '',  
        permission_id: '2',
    });

    const [users, setUsers] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [isModalOpen, setIsModalOpen] = useState(false); // Controla o modal

    useEffect(() => {
        async function loadUsers() {
            setLoading(true);
            try {
                const usersData = await fetchUsers();
                setUsers(usersData);
            } catch (err) {
                setErrorMessage('Erro ao buscar usuários: ' + err.message);
            } finally {
                setLoading(false);
            }
        }
        loadUsers();
    }, []);

    const handleChange = (e) => {
        const value = e.target.name === 'permission_id' ? parseInt(e.target.value) : e.target.value;
        setUser({ ...user, [e.target.name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Verificação de senha
        if (user.password !== user.confirmPassword) {
            toast.error('As senhas não coincidem.');
            return;
        }

        setIsSubmitting(true);
        try {
            await createUser(user);
            setUser({ nome: '', email: '', password: '', confirmPassword: '', permission_id: 1 });
            toast.success('Usuário criado com sucesso!');
            const usersData = await fetchUsers();
            setUsers(usersData);
            setIsModalOpen(false); // Fecha o modal após criar o usuário
        } catch (error) {
            const errMsg = error.message || 'Erro ao criar usuário.';
            setErrorMessage(errMsg);
            toast.error(errMsg);
            console.error(error);
        } finally {
            setIsSubmitting(false);
        }
    };

    const handleDeleteUser = async (userId) => {
        try {
            await deleteUser(userId);
            toast.success('Usuário excluído com sucesso!');
            const updatedUsers = users.filter(user => user.id !== userId);
            setUsers(updatedUsers);
        } catch (error) {
            toast.error('Erro ao excluir usuário.');
            console.error(error);
        }
    };

    return (
        <div className="formContainer">
            {isSubmitting && (
                <div className="loading-overlay">
                    <ClipLoader color="#007bff" size={60} />
                </div>
            )}

            <button
                className="openFormButton"
                onClick={() => setIsModalOpen(true)}  // Abre o modal ao clicar no botão
            >
                Criar Usuário
            </button>

            {isModalOpen && (
                <div className="modal-overlay">
                    <div className="modal">
                        <button
                            className="closeModalButton"
                            onClick={() => setIsModalOpen(false)}  // Fecha o modal ao clicar no botão fechar
                        >
                            Fechar
                        </button>
                        <form onSubmit={handleSubmit} className="user-form">
                            <h2>Criar Usuário</h2>
                            <div className="inputField">
                                <label>Nome:</label>
                                <input type="text" name="nome" value={user.nome} onChange={handleChange} required />
                            </div>
                            <div className="inputField">
                                <label>Email:</label>
                                <input type="email" name="email" value={user.email} onChange={handleChange} required />
                            </div>
                            <div className="inputField">
                                <label>Senha:</label>
                                <input type="password" name="password" value={user.password} onChange={handleChange} required />
                            </div>
                            <div className="inputField">
                                <label>Confirmar Senha:</label>
                                <input
                                    type="password"
                                    name="confirmPassword"
                                    value={user.confirmPassword}
                                    onChange={handleChange}
                                    required
                                />
                            </div>
                            <div className="inputField">
                                <label>Permissão:</label>
                                <select name="permission_id" value={user.permission_id} onChange={handleChange} required>
                                    <option value={1}>Total</option>
                                    <option value={2}>Limitada</option>
                                </select>
                            </div>
                            <button type="submit" className="submitButton" disabled={isSubmitting}>
                                {isSubmitting ? 'Enviando...' : 'Criar Usuário'}
                            </button>
                        </form>
                    </div>
                </div>
            )}

            <ToastContainer />
            {errorMessage && <div style={{ color: 'red' }}>{errorMessage}</div>}

            <h2>Lista de Usuários</h2>
            {loading ? (
                <ClipLoader color="#007bff" size={60} /> 
            ) : (
                <table className="user-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Nome</th>
                            <th>Email</th>
                            <th>Permissão</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.id}>
                                <td>{user.id}</td>
                                <td>{user.nome}</td>
                                <td>{user.email}</td>
                                <td>{parseInt(user.permission_id) === 1 ? 'Permissão total' : 'Permissão limitada'}</td>
                                <td>
                                    <button
                                        className="deleteButton"
                                        onClick={() => handleDeleteUser(user.id)}
                                    >
                                        Excluir
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default CreateUserComponent;
