import '../../components/headerLogin/HeaderLogin'; // Importação correta do HeaderLogin
import '../../components/auth/Login'; // Importação correta do Login
import '../../components/menu/Menu'; // Importação correta do Menu
import '../home/Home'; // Importação correta do Home
import React, { useState, useEffect } from 'react'; // Importação de React, incluindo useState e useEffect
import './CreateUser.css'; // Importação do CSS
import { createUser, fetchUsers, deleteUser } from '../../services/userService'; // Certifique-se de que deleteUser está implementado no userService
import { toast, ToastContainer } from 'react-toastify';  // Importação do Toastify para exibir notificações
import 'react-toastify/dist/ReactToastify.css';  // Importação do CSS para as notificações do Toastify
import { ClipLoader } from 'react-spinners'; // Importação do ClipLoader do react-spinners

function CreateUserComponent() {
    const [user, setUser] = useState({
        nome: '',
        email: '',
        password: '',
        permission_id: '1', // Definido como '1' (Total) por padrão
    });

    const [users, setUsers] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [isFormVisible, setIsFormVisible] = useState(false); // Novo estado para controlar a visibilidade do formulário

    useEffect(() => {
        async function loadUsers() {
            setLoading(true);
            try {
                const usersData = await fetchUsers();
                setUsers(usersData);
            } catch (err) {
                setErrorMessage('Erro ao buscar usuários: ' + err.message);
            } finally {
                setLoading(false);
            }
        }
        loadUsers();
    }, []);

    const handleChange = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);
        try {
            await createUser(user);
            setUser({ nome: '', email: '', password: '', permission_id: '1' }); // Reseta o estado do formulário
            toast.success('Usuário criado com sucesso!');
            const usersData = await fetchUsers();
            setUsers(usersData);
            setIsFormVisible(false); // Oculta o formulário após criar o usuário
        } catch (error) {
            const errMsg = error.message || 'Erro ao criar usuário.';
            setErrorMessage(errMsg);
            toast.error(errMsg);
            console.error(error);
        } finally {
            setIsSubmitting(false);
        }
    };

    const handleDeleteUser = async (userId) => {
        try {
            await deleteUser(userId);
            toast.success('Usuário excluído com sucesso!');
            const updatedUsers = users.filter(user => user.id !== userId);
            setUsers(updatedUsers);
        } catch (error) {
            toast.error('Erro ao excluir usuário.');
            console.error(error);
        }
    };

    return (
        <div className="formContainer">
            {/* Mostrar a tela de carregamento se o formulário estiver sendo enviado */}
            {isSubmitting && (
                <div className="loading-overlay">
                    <ClipLoader color="#007bff" size={60} />
                </div>
            )}

            {/* Botão para alternar a visibilidade do formulário */}
            <button
                className="openFormButton"
                onClick={() => setIsFormVisible(!isFormVisible)}
            >
                {isFormVisible ? 'Fechar Formulário' : 'Criar Usuário'}
            </button>

            {/* Renderiza o formulário condicionalmente */}
            {isFormVisible && (
                <form onSubmit={handleSubmit} className="user-form">
                    <h2>Criar Usuário</h2>
                    <div className="inputField">
                        <label>Nome:</label>
                        <input type="text" name="nome" value={user.nome} onChange={handleChange} required />
                    </div>
                    <div className="inputField">
                        <label>Email:</label>
                        <input type="email" name="email" value={user.email} onChange={handleChange} required />
                    </div>
                    <div className="inputField">
                        <label>Senha:</label>
                        <input type="password" name="password" value={user.password} onChange={handleChange} required />
                    </div>
                    <div className="inputField">
                        <label>Permissão:</label>
                        <select name="permission_id" value={user.permission_id} onChange={handleChange} required>
                            <option value="1">Total</option> {/* Opção Total */}
                            <option value="2">Limitada</option> {/* Opção Limitada */}
                        </select>
                    </div>
                    <button type="submit" className="submitButton" disabled={isSubmitting}>
                        {isSubmitting ? 'Enviando...' : 'Criar Usuário'}
                    </button>
                </form>
            )}

            <ToastContainer />
            {errorMessage && <div style={{ color: 'red' }}>{errorMessage}</div>}

            <h2>Lista de Usuários</h2>
            {loading ? (
                <ClipLoader color="#007bff" size={60} /> // Usando o spinner durante o carregamento da lista de usuários
            ) : (
                <table className="user-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Nome</th>
                            <th>Email</th>
                            <th>Permissão</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.id}>
                                <td>{user.id}</td>
                                <td>{user.nome}</td>
                                <td>{user.email}</td>
                                {/* Comparação ajustada para verificar permission_id como número */}
                                <td>{user.permission_id == 1 ? 'Permissão total' : 'Permissão limitada'}</td>
                                <td>
                                    <button
                                        className="deleteButton"
                                        onClick={() => handleDeleteUser(user.id)}
                                    >
                                        Excluir
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default CreateUserComponent;
