import React, { useState } from 'react';
import './Menu.css'; // Certifique-se de que seu CSS esteja corretamente configurado
import { FaChevronDown, FaChevronRight } from 'react-icons/fa'; // Ícones adicionados

function Menu() {
    const [openSubItem, setOpenSubItem] = useState(null); // Estado para controlar o sub-item aberto

    // Função para alternar a visibilidade dos sub-itens
    const toggleSubItem = (index) => {
        setOpenSubItem(openSubItem === index ? null : index); // Alterna o sub-item entre aberto e fechado
    };

    return (
        <div className="menu-container">
            <ul>
                <li className={openSubItem === 1 ? 'open' : ''} onClick={() => toggleSubItem(1)}>
                    <span>Agrupamento do Menu 1</span>
                    {openSubItem === 1 ? <FaChevronDown className="chevron-icon" /> : <FaChevronRight className="chevron-icon" />}
                    {openSubItem === 1 && (
                        <ul className="submenu">
                            <li>Item do Menu 1</li>
                            <li>Item do Menu 2</li>
                            <li>Item do Menu 3</li>
                            <li>Item do Menu 4</li>
                        </ul>
                    )}
                </li>
                <li className={openSubItem === 2 ? 'open' : ''} onClick={() => toggleSubItem(2)}>
                    <span>Agrupamento do Menu 2</span>
                    {openSubItem === 2 ? <FaChevronDown className="chevron-icon" /> : <FaChevronRight className="chevron-icon" />}
                </li>
                <li className={openSubItem === 3 ? 'open' : ''} onClick={() => toggleSubItem(3)}>
                    <span>Agrupamento do Menu 3</span>
                    {openSubItem === 3 ? <FaChevronDown className="chevron-icon" /> : <FaChevronRight className="chevron-icon" />}
                </li>
                <li className={openSubItem === 4 ? 'open' : ''} onClick={() => toggleSubItem(4)}>
                    <span>Agrupamento do Menu 4</span>
                    {openSubItem === 4 ? <FaChevronDown className="chevron-icon" /> : <FaChevronRight className="chevron-icon" />}
                </li>
            </ul>
        </div>
    );
}

export default Menu;
