import React, { useState } from 'react';
import './Menu.css'; // Importa o CSS
import { FaHome, FaBook, FaGraduationCap, FaCalendarAlt, FaComments, FaQuestionCircle, FaBars, FaAngleDown } from 'react-icons/fa';

const Menu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isSubmenuOpen, setIsSubmenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleSubmenu = () => {
    setIsSubmenuOpen(!isSubmenuOpen);
  };

  return (
    <div>
      {/* Botão hambúrguer */}
      <button className="hamburger" onClick={toggleMenu}>
        <FaBars />
      </button>

      {/* Menu lateral */}
      <div className={`menu-container ${isMenuOpen ? 'open' : ''}`}>
        <ul className="menu">
          <li>
            <FaHome />
            <span>Home</span>
          </li>
          <li className={`menu-item ${isSubmenuOpen ? 'open' : ''}`} onClick={toggleSubmenu}>
            <FaBook />
            <span>Catálogo</span>
            <FaAngleDown className={`submenu-icon ${isSubmenuOpen ? 'open' : ''}`} />
            {/* Submenu */}
            <ul className={`submenu ${isSubmenuOpen ? 'open' : ''}`}>
              <li>JavaScript</li>
              <li>Node.js</li>
              <li>React</li>
            </ul>
          </li>
          <li>
            <FaGraduationCap />
            <span>Pós-Graduação</span>
          </li>
          <li>
            <FaCalendarAlt />
            <span>Eventos</span>
          </li>
          <li>
            <FaComments />
            <span>Fórum</span>
          </li>
        </ul>

        {/* Itens do menu inferior */}
        <ul className="menu-bottom">
          <li>
            <FaQuestionCircle />
            <span>Ajuda</span>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Menu;
