import React, { useState } from 'react';
import './Menu.css';

function Menu() {
    const [isOpen, setIsOpen] = useState(false); // Estado para controlar se o menu está aberto

    const toggleMenu = () => {
        setIsOpen(!isOpen); // Alterna o estado entre aberto e fechado
    };

    return (
        <div className="menu-container">
            
            <button className="hamburger-menu" onClick={toggleMenu}>
                &#9776; {/* Ícone de menu hambúrguer */}
            </button>
            {/* O menu será exibido ou ocultado com base no estado isOpen */}
            <ul className={`menu-items ${isOpen ? 'open' : ''}`}>
                <li>Sub-item 1</li>
                <li>Sub-item 2</li>
                <li>Sub-item 3</li>
            </ul>
        </div>
    );
}

export default Menu;
