import React, { useState } from 'react';
import './Menu.css';
import { FaHome, FaBook, FaGraduationCap, FaCalendarAlt, FaComments, FaQuestionCircle, FaBars } from 'react-icons/fa';

const Menu = () => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div>
            {/* Botão hamburguer */}
            <button className="hamburger" onClick={toggleMenu}>
                <FaBars />
            </button>
            
            {/* Menu lateral */}
            <div className={`menu-container ${isOpen ? 'open' : ''}`}>
                <ul className="menu">
                    <li>
                        <FaHome />
                        <span>Home</span>
                    </li>
                    <li className="active">
                        <FaBook />
                        <span>Catálogo</span>
                    </li>
                    <li>
                        <FaGraduationCap />
                        <span>Pós-Graduação</span>
                    </li>
                    <li>
                        <FaCalendarAlt />
                        <span>Eventos</span>
                    </li>
                    <li>
                        <FaComments />
                        <span>Fórum</span>
                    </li>
                </ul>
                <ul className="menu-bottom">
                    <li>
                        <FaQuestionCircle />
                        <span>Ajuda</span>
                    </li>
                </ul>
            </div>
        </div>
    );
};

export default Menu;
