import React, { useState } from 'react';
import './Sidebar.css';
import { FaHome, FaBook, FaGraduationCap, FaCalendarAlt, FaComments } from 'react-icons/fa';

const Sidebar = () => {
    const [activeItem, setActiveItem] = useState('catalogo');

    const handleItemClick = (item) => {
        setActiveItem(item);
    };

    return (
        <div className="sidebar">
            <div className="logo">
                <img src="logo_url_aqui" alt="Logo" />
            </div>
            <ul className="menu">
                <li 
                    className={activeItem === 'home' ? 'active' : ''}
                    onClick={() => handleItemClick('home')}
                >
                    <FaHome />
                    <span>Home</span>
                </li>
                <li 
                    className={activeItem === 'catalogo' ? 'active' : ''}
                    onClick={() => handleItemClick('catalogo')}
                >
                    <FaBook />
                    <span>Catálogo</span>
                </li>
                <li 
                    className={activeItem === 'graduacao' ? 'active' : ''}
                    onClick={() => handleItemClick('graduacao')}
                >
                    <FaGraduationCap />
                    <span>Pós-Graduação</span>
                </li>
                <li 
                    className={activeItem === 'eventos' ? 'active' : ''}
                    onClick={() => handleItemClick('eventos')}
                >
                    <FaCalendarAlt />
                    <span>Eventos</span>
                </li>
                <li 
                    className={activeItem === 'forum' ? 'active' : ''}
                    onClick={() => handleItemClick('forum')}
                >
                    <FaComments />
                    <span>Fórum</span>
                </li>
            </ul>
        </div>
    );
};

export default Sidebar;
