import '../../components/headerLogin/HeaderLogin'; // Importação correta do HeaderLogin
import '../../components/auth/Login'; // Importação correta do Login
import '../../components/menu/Menu'; // Importação correta do Menu
import '../home/Home'; // Importação correta do Home
import React, { useState } from 'react';
import './CreateUser.css'; // Ensure this is the correct path

const CreateUser = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/users', { // Ajuste a URL conforme necessário
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      if (response.ok) {
        console.log('Usuário criado:', data);
        // Você pode redirecionar o usuário ou limpar o formulário aqui
      } else {
        throw new Error(data.message || 'Erro ao criar usuário');
      }
    } catch (error) {
      console.error('Erro na submissão:', error.message);
    }
  };

  return (
    <div className="create-user-form">
      <h1>Create New User</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
        </label>
        <br />
        <label>
          Email:
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
        </label>
        <br />
        <label>
          Password:
          <input type="password" name="password" value={formData.password} onChange={handleChange} />
        </label>
        <br />
        <button type="submit">Create User</button>
      </form>
    </div>
  );
};

export default CreateUser;
