import '../../components/headerLogin/HeaderLogin'; // Importação correta do HeaderLogin
import '../../components/auth/Login'; // Importação correta do Login
import '../../components/menu/Menu'; // Importação correta do Menu
import '../home/Home'; // Importação correta do Home
import React, { useState } from 'react';
import './CreateUser.css'; // Ensure this is the correct path
import { createUser } from '../services/userService';


function CreateUser() {
    const [formData, setFormData] = useState({ name: '', email: '', password: '' });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await createUser(formData);
            alert('Usuário criado com sucesso!');
            setFormData({ name: '', email: '', password: '' }); // Limpar o formulário após sucesso
        } catch (error) {
            alert(error.message || 'Falha ao criar usuário!');
        }
    };
    

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Nome" />
            <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" />
            <input type="password" name="password" value={formData.password} onChange={handleChange} placeholder="Senha" />
            <button type="submit">Criar Usuário</button>
        </form>
    );
}

export default CreateUser;
