import React, { useState } from 'react';
import './Menu.css';

function Menu() {
    const [isOpen, setIsOpen] = useState(false); // Estado para controlar se o menu está aberto
    const [openSubItem, setOpenSubItem] = useState(null); // Estado para controlar o sub-item aberto

    const toggleMenu = () => {
        setIsOpen(!isOpen); // Alterna o estado entre aberto e fechado
    };

    // Função para alternar a visibilidade dos sub-itens
    const toggleSubItem = (index) => {
        setOpenSubItem(openSubItem === index ? null : index); // Alterna o sub-item entre aberto e fechado
    };

    return (
        <div className="menu-container">
            <h1>Bem-vindo ao Sistema de Ferramenta Manual</h1>
            {/* O botão hamburger só aparece em telas menores (CSS controlado via media query) */}
            <button className="hamburger-menu" onClick={toggleMenu}>
                &#9776; {/* Ícone de menu hambúrguer */}
            </button>
            {/* O menu será exibido ou ocultado com base no estado isOpen */}
            <ul className={`menu-items ${isOpen ? 'open' : ''}`}>
                <li onClick={() => toggleSubItem(1)}>
                    Sub-item 1
                    {/* Sub-itens de Sub-item 1 */}
                    {openSubItem === 1 && (
                        <ul className="submenu">
                            <li>Sub-item 1.1</li>
                            <li>Sub-item 1.2</li>
                            <li>Sub-item 1.3</li>
                        </ul>
                    )}
                </li>
                <li onClick={() => toggleSubItem(2)}>
                    Sub-item 2
                    {/* Sub-itens de Sub-item 2 */}
                    {openSubItem === 2 && (
                        <ul className="submenu">
                            <li>Sub-item 2.1</li>
                            <li>Sub-item 2.2</li>
                        </ul>
                    )}
                </li>
                <li onClick={() => toggleSubItem(3)}>
                    Sub-item 3
                    {/* Sub-itens de Sub-item 3 */}
                    {openSubItem === 3 && (
                        <ul className="submenu">
                            <li>Sub-item 3.1</li>
                            <li>Sub-item 3.2</li>
                        </ul>
                    )}
                </li>
            </ul>
        </div>
    );
}

export default Menu;
