import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Menu.css';
import { FaHome, FaBook, FaQuestionCircle, FaBars, FaUserCircle, FaUserPlus } from 'react-icons/fa';

const Menu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [username, setUsername] = useState('Guest');
  const [permission, setPermission] = useState(0); // Permissão padrão é 0 (sem permissão)

  useEffect(() => {
    const storedUsername = localStorage.getItem('username');
    const storedPermission = localStorage.getItem('permission');

    if (storedUsername && storedUsername !== 'undefined') {
      setUsername(storedUsername);
    }

    if (storedPermission) {
      const perm = parseInt(storedPermission, 10);
      setPermission(perm);
    }
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen); // Alterna o estado do menu
  };

  return (
    <div>
      <button className="hamburger" onClick={toggleMenu}>
        <FaBars />
      </button>

      <div className={`menu-container ${isMenuOpen ? 'open' : ''}`}>
        <div className="user-info">
          <FaUserCircle style={{ marginRight: '10px', fontSize: '24px' }} />
          <span>{username}</span>
        </div>

        <ul className="menu">
          <li>
            <FaHome />
            <Link to="/home">Home</Link>
          </li>
          <li>
            <FaBook />
            <Link to="/layouts">Layouts</Link>
          </li>
          {permission === 1 && (
            <li>
              <FaUserPlus />
              <Link to="/add-user">Add User</Link>
            </li>
          )}
        </ul>

        <ul className="menu-bottom">
          <li>
            <FaQuestionCircle />
            <Link to="/ajuda">Ajuda</Link>
          </li>
        </ul>
      </div>

      {isMenuOpen && <div className="overlay" onClick={toggleMenu}></div>}
    </div>
  );
};

export default Menu;
