import React from 'react';
import { FaHome, FaBook, FaGraduationCap, FaCalendarAlt, FaComments } from 'react-icons/fa';

const Menu = ({ activeItem, onItemClick }) => {
    return (
        <ul className="menu">
            <li 
                className={activeItem === 'home' ? 'active' : ''}
                onClick={() => onItemClick('home')}
            >
                <FaHome />
                <span>Home</span>
            </li>
            <li 
                className={activeItem === 'catalogo' ? 'active' : ''}
                onClick={() => onItemClick('catalogo')}
            >
                <FaBook />
                <span>Catálogo</span>
            </li>
            <li 
                className={activeItem === 'graduacao' ? 'active' : ''}
                onClick={() => onItemClick('graduacao')}
            >
                <FaGraduationCap />
                <span>Pós-Graduação</span>
            </li>
            <li 
                className={activeItem === 'eventos' ? 'active' : ''}
                onClick={() => onItemClick('eventos')}
            >
                <FaCalendarAlt />
                <span>Eventos</span>
            </li>
            <li 
                className={activeItem === 'forum' ? 'active' : ''}
                onClick={() => onItemClick('forum')}
            >
                <FaComments />
                <span>Fórum</span>
            </li>
        </ul>
    );
};

export default Menu;
