function CreateUserComponent() {
    const [user, setUser] = useState({
        nome: '',
        email: '',
        password: '',
        permission_id: '1'  // Valor padrão para 'Permissão total'
    });
    const [errorMessage, setErrorMessage] = useState('');

    const handleChange = (e) => {
        setUser({...user, [e.target.name]: e.target.value});
        setErrorMessage(''); // Limpar a mensagem de erro quando o usuário altera qualquer input
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await createUser(user);
            if (response.status === 200) {
                alert('Usuário criado com sucesso!');
                console.log(response);
                // Redefinir o formulário ou redirecionar o usuário
            } else {
                throw new Error(response.data.message);
            }
        } catch (error) {
            setErrorMessage(error.message || 'Erro ao criar usuário.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="nome" value={user.nome} onChange={handleChange} placeholder="Nome" />
            <input type="email" name="email" value={user.email} onChange={handleChange} placeholder="Email" />
            <input type="password" name="password" value={user.password} onChange={handleChange} placeholder="Senha" />
            <select name="permission_id" value={user.permission_id} onChange={handleChange}>
                <option value="1">Permissão total</option>
                <option value="2">Permissão limitada</option>
            </select>
            <button type="submit">Criar Usuário</button>
            {errorMessage && <div style={{ color: 'red' }}>{errorMessage}</div>}
        </form>
    );
}

export default CreateUserComponent;
