// Função auxiliar para verificar a resposta do servidor
async function checkResponse(response) {
    const errorData = await response.json();
    if (!response.ok) {
        if (response.status === 409) {
            throw new Error('Usuário já existe.');
        }
        throw new Error(errorData.message || response.statusText);
    }
    return errorData; // Retorna os dados se a resposta for bem-sucedida
}

// Função para criar usuário
export async function createUser(user) {
    try {
        const response = await fetch('http://localhost:3000/api/users', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(user),
        });

        return await checkResponse(response); // Verifica e retorna a resposta processada
    } catch (error) {
        console.error('Erro ao criar usuário:', error);
        throw error; // Propaga o erro para ser tratado por quem chamou essa função
    }
}

// Função para buscar usuários
export async function fetchUsers() {
    try {
        const response = await fetch('http://localhost:3000/api/users', {
            method: 'GET',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
        });

        return await checkResponse(response); // Verifica e retorna a resposta processada
    } catch (error) {
        console.error('Erro ao buscar usuários:', error);
        throw error; // Propaga o erro para ser tratado por quem chamou essa função
    }
}

// Função para deletar um usuário
export async function deleteUser(userId) {
    try {
        const response = await fetch(`http://localhost:3000/api/users/${userId}`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
        });

        return await checkResponse(response); // Verifica e retorna a resposta processada
    } catch (error) {
        console.error('Erro ao excluir usuário:', error);
        throw error; // Propaga o erro para ser tratado por quem chamou essa função
    }
}
