import React, { useState } from 'react';
import './Menu.css';
import { FaHome, FaBook, FaGraduationCap, FaCalendarAlt, FaComments, FaQuestionCircle, FaBars, FaAngleDown } from 'react-icons/fa';

const Menu = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [isSubmenuOpen, setIsSubmenuOpen] = useState(false); // Estado para controlar o submenu

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const toggleSubmenu = () => {
        setIsSubmenuOpen(!isSubmenuOpen); // Alterna a visibilidade do submenu
    };

    return (
        <div>
            {/* Botão hambúrguer */}
            <button className="hamburger" onClick={toggleMenu}>
                <FaBars />
            </button>

            {/* Menu lateral */}
            <div className={`menu-container ${isMenuOpen ? 'open' : ''}`}>
                <ul className="menu">
                    <li>
                        <FaHome />
                        <span className="menu-text">Home</span>
                    </li>
                    {/* Item com Submenu */}
                    <li className={`menu-item ${isSubmenuOpen ? 'open' : ''}`} onClick={toggleSubmenu}>
                        <FaBook />
                        <span className="menu-text">Catálogo</span>
                        <FaAngleDown className="submenu-icon" />
                        {/* Submenu */}
                        {isSubmenuOpen && (
                            <ul className="submenu">
                                <li>JavaScript</li>
                                <li>Node.js</li>
                                <li>React</li>
                            </ul>
                        )}
                    </li>

                    <li>
                        <FaGraduationCap />
                        <span className="menu-text">Pós-Graduação</span>
                    </li>
                    <li>
                        <FaCalendarAlt />
                        <span className="menu-text">Eventos</span>
                    </li>
                    <li>
                        <FaComments />
                        <span className="menu-text">Fórum</span>
                    </li>
                </ul>
                <ul className="menu-bottom">
                    <li>
                        <FaQuestionCircle />
                        <span className="menu-text">Ajuda</span>
                    </li>
                </ul>
            </div>
        </div>
    );
};

export default Menu;
