import React, { useState } from 'react';
import './Menu.css';
import { FaHome, FaBook, FaGraduationCap, FaCalendarAlt, FaComments, FaDiscord, FaQuestionCircle } from 'react-icons/fa';

function Menu() {
    const [isOpen, setIsOpen] = useState(false);

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className={`sidebar ${isOpen ? 'open' : ''}`}>
            <div className="menu-toggle" onClick={toggleMenu}>
                <FaHome /> {/* Ícone de "hamburger" ou outro ícone de sua escolha */}
            </div>
            <div className="logo">ROCKETS</div>
            <ul className="menu-items">
                <li className="menu-item">
                    <FaHome />
                    <span>Home</span>
                </li>
                <li className="menu-item">
                    <FaBook />
                    <span>Catálogo</span>
                </li>
                <li className="menu-item">
                    <FaGraduationCap />
                    <span>Pós-Graduação</span>
                </li>
                <li className="menu-item">
                    <FaCalendarAlt />
                    <span>Eventos</span>
                    <span className="badge">1</span> {/* Badge de notificação */}
                </li>
                <li className="menu-item">
                    <FaComments />
                    <span>Fórum</span>
                </li>
            </ul>
            <ul className="menu-footer">
                <li className="menu-item">
                    <FaDiscord />
                    <span>Comunidade</span>
                </li>
                <li className="menu-item">
                    <FaQuestionCircle />
                    <span>Ajuda</span>
                </li>
            </ul>
        </div>
    );
}

export default Menu;
