import React, { useState } from 'react';
import './Menu.css';
import { FaChevronDown, FaChevronRight } from 'react-icons/fa'; // Certifique-se de ter ícones para expansão

function Menu() {
    const [isOpen, setIsOpen] = useState(false); // Controla a visibilidade do menu completo
    const [openSubItem, setOpenSubItem] = useState(null); // Controla qual submenu está aberto

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    const toggleSubItem = (index) => {
        // Alterna apenas o submenu correspondente, fechando outros se estiverem abertos
        if (openSubItem === index) {
            setOpenSubItem(null);
        } else {
            setOpenSubItem(index);
        }
    };

    return (
        <div className="menu-container">
            <h1>Bem-vindo ao Sistema de Ferramenta Manual</h1>
            <button className="hamburger-menu" onClick={toggleMenu}>
                &#9776;
            </button>
            <ul className={`menu-items ${isOpen ? 'open' : ''}`}>
                <li className={`${openSubItem === 1 ? 'open' : ''}`} onClick={() => toggleSubItem(1)}>
                    <span>Sub-item 1</span> {openSubItem === 1 ? <FaChevronDown /> : <FaChevronRight />}
                    {openSubItem === 1 && (
                        <ul className="submenu">
                            <li>Sub-item 1.1</li>
                            <li>Sub-item 1.2</li>
                            <li>Sub-item 1.3</li>
                        </ul>
                    )}
                </li>
                {/* Repita a estrutura acima para outros itens do menu conforme necessário */}
            </ul>
        </div>
    );
}

export default Menu;
