import React, { useState } from 'react';
import './Menu.css';
import { FaChevronDown, FaChevronRight, FaHome, FaBook, FaUserGraduate } from 'react-icons/fa';

function Menu() {
    const [openItem, setOpenItem] = useState(null);

    const toggleItem = (index) => {
        setOpenItem(openItem === index ? null : index); // Alterna entre abrir e fechar sub-itens
    };

    return (
        <div className="menu-container">
            <h1>Menu Lateral</h1>
            <ul>
                <li onClick={() => toggleItem(1)} className={openItem === 1 ? 'open' : ''}>
                    <FaHome />
                    <span>Item 1</span>
                    {openItem === 1 ? <FaChevronDown /> : <FaChevronRight />}
                    {openItem === 1 && (
                        <ul className="submenu">
                            <li>Sub-item 1.1</li>
                            <li>Sub-item 1.2</li>
                            <li>Sub-item 1.3</li>
                        </ul>
                    )}
                </li>
                <li onClick={() => toggleItem(2)} className={openItem === 2 ? 'open' : ''}>
                    <FaBook />
                    <span>Item 2</span>
                    {openItem === 2 ? <FaChevronDown /> : <FaChevronRight />}
                    {openItem === 2 && (
                        <ul className="submenu">
                            <li>Sub-item 2.1</li>
                            <li>Sub-item 2.2</li>
                        </ul>
                    )}
                </li>
                <li onClick={() => toggleItem(3)} className={openItem === 3 ? 'open' : ''}>
                    <FaUserGraduate />
                    <span>Item 3</span>
                    {openItem === 3 ? <FaChevronDown /> : <FaChevronRight />}
                    {openItem === 3 && (
                        <ul className="submenu">
                            <li>Sub-item 3.1</li>
                            <li>Sub-item 3.2</li>
                        </ul>
                    )}
                </li>
            </ul>
        </div>
    );
}

export default Menu;
