import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Menu.css';
import { FaHome, FaBook, FaBars, FaUserCircle, FaUserPlus, FaSignOutAlt, FaFileAlt } from 'react-icons/fa';

const Menu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [username, setUsername] = useState('Guest');
  const [permission, setPermission] = useState(0);
  const [isLoading, setIsLoading] = useState(false); // Carregamento de logout
  const [isUserLoading, setIsUserLoading] = useState(false); // Novo estado para carregamento de usuários
  const navigate = useNavigate();

  // Carrega os dados do usuário
  useEffect(() => {
    const storedUsername = localStorage.getItem('username');
    const storedPermission = localStorage.getItem('permission');
  
    // Verifica se os dados estão no localStorage antes de definir o carregamento
    if (storedUsername && storedUsername !== 'undefined') {
      setUsername(storedUsername);
      setIsUserLoading(false); // Dados já disponíveis, não há necessidade de carregamento
    } else {
      setIsUserLoading(true); // Inicia o carregamento se os dados não estiverem disponíveis
    }
  
    // Carrega a permissão se disponível
    if (storedPermission) {
      const perm = parseInt(storedPermission, 10);
      setPermission(perm);
    }
  
    // Simula o carregamento apenas se necessário
    if (!storedUsername || storedUsername === 'undefined') {
      setTimeout(() => {
        setIsUserLoading(false); // Finaliza o carregamento dos dados
      }, 1000); // Simula 1 segundo de carregamento
    }
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleLogout = () => {
    setIsLoading(true); // Ativa o carregamento de logout

    // Simula um pequeno atraso antes de redirecionar
    setTimeout(() => {
      localStorage.removeItem('username');
      localStorage.removeItem('permission');

      // Redireciona o usuário para a página de login
      navigate('/');

      setIsLoading(false); // Desativa o carregamento
    }, 1500); // Simula 1.5s de carregamento
  };

  return (
    <div>
      {isLoading ? ( // Tela de carregamento durante o logout
        <div className="loading-screen">
          <div className="spinner"></div>
        </div>
      ) : (
        <>
          <button className="hamburger" onClick={toggleMenu}>
            <FaBars />
          </button>

          <div className={`menu-container ${isMenuOpen ? 'open' : ''}`}>
            <div className="user-info">
              {isUserLoading ? ( // Exibe o carregamento durante a busca de dados do usuário
                <div className="spinner" style={{ width: '30px', height: '30px' }}></div>
              ) : (
                <>
                  <FaUserCircle style={{ marginRight: '10px', fontSize: '24px' }} />
                  <span>{username}</span>
                </>
              )}
            </div>

            <ul className="menu">
              <li>
                <FaHome />
                <Link to="/home">Home</Link>
              </li>
              <li>
                <FaFileAlt />
                <Link to="">Ocorrencias</Link>
              </li>
              <li>
                <FaBook />
                <Link to="/layouts">Layouts</Link>
              </li>
              {permission === 1 && (
                <li>
                  <FaUserPlus />
                  <Link to="/add-user">Usuarios</Link>
                </li>
              )}
            </ul>

            <ul className="menu-bottom">
              <li>
                <FaSignOutAlt />
                <button className="logout-button" onClick={handleLogout}>
                  Sair
                </button>
              </li>
            </ul>
          </div>

          {isMenuOpen && <div className="overlay" onClick={toggleMenu}></div>}
        </>
      )}
    </div>
  );
};

export default Menu;
