import '../../components/headerLogin/HeaderLogin'; // Importação correta do HeaderLogin
import '../../components/auth/Login'; // Importação correta do Login
import '../../components/menu/Menu'; // Importação correta do Menu
import '../home/Home'; // Importação correta do Home
import React, { useState } from 'react';
import './CreateUser.css';
import { createUser } from '../../services/userService';
import { toast, ToastContainer } from 'react-toastify';  
import 'react-toastify/dist/ReactToastify.css'; 

function CreateUserComponent() {
    const [user, setUser] = useState({
        nome: '',
        email: '',
        password: '',
        permission_id: '1', // Valor padrão para 'Permissão total'
    });
    const [errorMessage, setErrorMessage] = useState('');

    const handleChange = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
        setErrorMessage(''); // Limpar mensagem de erro quando o usuário altera qualquer input
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await createUser(user);
            toast.success('Usuário criado com sucesso!');  // Exibir notificação de sucesso
            console.log(response);
            // Redefinir o formulário ou redirecionar o usuário
            setUser({ nome: '', email: '', password: '', permission_id: '1' });  // Limpar o formulário
        } catch (error) {
            setErrorMessage(error.message || 'Erro ao criar usuário.');
            toast.error(errorMessage);  // Exibir notificação de erro
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="nome"
                    value={user.nome}
                    onChange={handleChange}
                    placeholder="Nome"
                    required
                />
                <input
                    type="email"
                    name="email"
                    value={user.email}
                    onChange={handleChange}
                    placeholder="Email"
                    required
                />
                <input
                    type="password"
                    name="password"
                    value={user.password}
                    onChange={handleChange}
                    placeholder="Senha"
                    required
                />
                <select name="permission_id" value={user.permission_id} onChange={handleChange}>
                    <option value="1">Permissão total</option>
                    <option value="2">Permissão limitada</option>
                </select>
                <button type="submit">Criar Usuário</button>
                {errorMessage && <div style={{ color: 'red' }}>{errorMessage}</div>}
            </form>

            {/* Adicionar o container do Toastify */}
            <ToastContainer />
        </div>
    );
}

export default CreateUserComponent;
