const API_URL = 'http://localhost:3001/api/users';  // Ajuste a porta conforme necessário

export const createUser = async (userData) => {
    try {
        const response = await fetch(API_URL, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(userData)
        });
        const data = await response.json();
        if (!response.ok) {
            throw new Error(data.message || 'Erro ao criar usuário');
        }
        return data;  // Retorna os dados do usuário criado, assumindo que seu backend retorna isso
    } catch (error) {
        throw error;  // Lança o erro para ser tratado pelo componente
    }
};
