import '../../components/headerLogin/HeaderLogin'; // Importação correta do HeaderLogin
import '../../components/auth/Login'; // Importação correta do Login
import '../../components/menu/Menu'; // Importação correta do Menu
import '../home/Home'; // Importação correta do Home
import React, { useState } from 'react';
import './CreateUser.css'; // Ensure this is the correct path
import { createUser } from '../../services/userService';

function CreateUserComponent() {
    const [user, setUser] = useState({
        name: '',
        email: '',
        password: '',
        permission_id: '' // assumindo que você quer isso também
    });

    const handleChange = (e) => {
        setUser({...user, [e.target.name]: e.target.value});
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await createUser(user);
            alert('Usuário criado com sucesso!');
            console.log(response);
            // Redefinir o formulário ou redirecionar o usuário
        } catch (error) {
            alert('Erro ao criar usuário: ' + error.message);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="name" value={user.name} onChange={handleChange} placeholder="Nome" />
            <input type="email" name="email" value={user.email} onChange={handleChange} placeholder="Email" />
            <input type="password" name="password" value={user.password} onChange={handleChange} placeholder="Senha" />
            <input type="text" name="permission_id" value={user.permission_id} onChange={handleChange} placeholder="ID de Permissão" />
            <button type="submit">Criar Usuário</button>
        </form>
    );
}

export default CreateUserComponent;
