import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Menu.css';
import { FaHome, FaBook, FaQuestionCircle, FaBars, FaUserCircle, FaUserPlus } from 'react-icons/fa'; // Adicione FaUserPlus para o ícone de adicionar usuário


const Menu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [username, setUsername] = useState('');
  const [permission, setPermission] = useState(0); // Default como 0 ou outro valor que considere "sem permissão"

  useEffect(() => {
    const storedUsername = localStorage.getItem('username');
    const storedPermission = localStorage.getItem('permission');
    
    if (storedUsername && storedUsername !== 'undefined' && storedUsername !== '') {
      setUsername(storedUsername);
    } else {
      setUsername('Guest');
    }
    
    if (storedPermission) {
      setPermission(parseInt(storedPermission, 10));
    }
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className="menu">
      <button className="hamburger" onClick={toggleMenu}>
        <FaBars />
      </button>

      <div className={`menu-container ${isMenuOpen ? 'open' : ''}`}>
        <div className="user-info">
          <FaUserCircle style={{ marginRight: '10px', fontSize: '24px' }} />
          <span>{username}</span> {/* Exibe o nome do usuário */}
        </div>

        <ul className="menu">
          <li>
            <FaHome />
            <Link to="/home">Home</Link>
          </li>
          <li>
            <FaBook />
            <Link to="/layouts">Layouts</Link>
          </li>
          {permission === 1 && (
            <li>
              <FaUserPlus />
              <Link to="/add-user">Add User</Link> {/* Link para adicionar usuário */}
            </li>
          )}
        </ul>

        <ul className="menu-bottom">
          <li>
            <FaQuestionCircle />
            <Link to="/ajuda">Ajuda</Link>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Menu;
