import '../../components/headerLogin/HeaderLogin'; // Importação correta do HeaderLogin
import '../../components/auth/Login'; // Importação correta do Login
import '../../components/menu/Menu'; // Importação correta do Menu
import '../home/Home'; // Importação correta do Home
import React, { useState } from 'react';
import './CreateUser.css';
import { createUser } from '../../services/userService';
import { toast, ToastContainer } from 'react-toastify';  
import 'react-toastify/dist/ReactToastify.css'; 


function CreateUserComponent() {
    const [user, setUser] = useState({
        nome: '',
        email: '',
        password: '',
        permission_id: '1',
    });
    const [users, setUsers] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        const loadUsers = async () => {
            try {
                const usersData = await fetchUsers(); // Assegure-se de que esta função está implementada corretamente
                setUsers(usersData);
            } catch (err) {
                setErrorMessage('Erro ao buscar usuários: ' + err.message);
            }
        };
        loadUsers();
    }, []);

    const handleChange = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const newUser = await createUser(user);
            setUsers([...users, newUser]); // Adicione o novo usuário à lista
            setUser({ nome: '', email: '', password: '', permission_id: '1' }); // Limpa o formulário
            toast.success('Usuário criado com sucesso!');
        } catch (error) {
            const errMsg = error.message || 'Erro ao criar usuário.';
            setErrorMessage(errMsg);
            toast.error(errMsg);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                {/* Campos do formulário, mantidos conforme o original */}
            </form>
            <ToastContainer />
            {errorMessage && <div style={{ color: 'red' }}>{errorMessage}</div>}
            <h2>Lista de Usuários</h2>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>Email</th>
                        <th>Permissão</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.nome}</td>
                            <td>{user.email}</td>
                            <td>{user.permission_id === '1' ? 'Total' : 'Limitada'}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default CreateUserComponent;
