import React, { useState } from 'react';
import './Menu.css'; // Certifique-se de que seu CSS esteja corretamente configurado
import { FaChevronDown, FaChevronRight, FaBars } from 'react-icons/fa'; // Ícone para o menu hambúrguer

function Menu() {
    const [isOpen, setIsOpen] = useState(false); // Estado para controlar se o menu está aberto
    const [openSubItem, setOpenSubItem] = useState(null); // Estado para controlar o sub-item aberto
    const [menuVisible, setMenuVisible] = useState(false); // Estado para mostrar/esconder o menu em telas pequenas

    const toggleMenu = () => {
        setMenuVisible(!menuVisible); // Alterna entre mostrar/esconder o menu
    };

    // Função para alternar a visibilidade dos sub-itens
    const toggleSubItem = (index) => {
        setOpenSubItem(openSubItem === index ? null : index); // Alterna o sub-item entre aberto e fechado
    };

    return (
        <>
            {/* Botão de menu hambúrguer para telas menores */}
            <button className="hamburger-menu" onClick={toggleMenu}>
                <FaBars />
            </button>

            {/* Menu container com a classe "hidden" ou "open" para controlar visibilidade */}
            <div className={`menu-container ${menuVisible ? 'open' : 'hidden'}`}>
                <h1>Bem-vindo ao Sistema de Ferramenta Manual</h1>
                <ul className={`menu-items ${isOpen ? 'open' : ''}`}>
                    <li className={openSubItem === 1 ? 'open' : ''} onClick={() => toggleSubItem(1)}>
                        <span>Item 1</span>
                        {openSubItem === 1 ? <FaChevronDown /> : <FaChevronRight />}
                        {openSubItem === 1 && (
                            <ul className="submenu">
                                <li>Sub-item 1.1</li>
                                <li>Sub-item 1.2</li>
                                <li>Sub-item 1.3</li>
                            </ul>
                        )}
                    </li>
                    <li className={openSubItem === 2 ? 'open' : ''} onClick={() => toggleSubItem(2)}>
                        <span>Item 2</span>
                        {openSubItem === 2 ? <FaChevronDown /> : <FaChevronRight />}
                        {openSubItem === 2 && (
                            <ul className="submenu">
                                <li>Sub-item 2.1</li>
                                <li>Sub-item 2.2</li>
                            </ul>
                        )}
                    </li>
                    <li className={openSubItem === 3 ? 'open' : ''} onClick={() => toggleSubItem(3)}>
                        <span>Item 3</span>
                        {openSubItem === 3 ? <FaChevronDown /> : <FaChevronRight />}
                        {openSubItem === 3 && (
                            <ul className="submenu">
                                <li>Sub-item 3.1</li>
                                <li>Sub-item 3.2</li>
                            </ul>
                        )}
                    </li>
                </ul>
            </div>
        </>
    );
}

export default Menu;
