import React, { useState } from 'react';
import './Menu.css'; // Importa o CSS
import { FaChevronRight } from 'react-icons/fa'; // Ícone da seta

const Menu = () => {
  const [isNordesteOpen, setIsNordesteOpen] = useState(false); // Estado para controlar o submenu do Nordeste

  const toggleSubmenu = () => {
    setIsNordesteOpen(!isNordesteOpen);
  };

  return (
    <div>
      {/* Menu lateral */}
      <div className="menu-container">
        <ul className="menu">
          <li className="menu-item">
            Norte
          </li>
          <li className={`menu-item ${isNordesteOpen ? 'open' : ''}`} onClick={toggleSubmenu}>
            <span>Nordeste</span>
            <FaChevronRight className={`submenu-icon ${isNordesteOpen ? 'open' : ''}`} />
            {/* Submenu */}
            <ul className={`submenu ${isNordesteOpen ? 'open' : ''}`}>
              <li>Pernambuco</li>
              <li>Maranhão</li>
              <li>Sergipe</li>
              <li>Bahia</li>
            </ul>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Menu;
