import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import HeaderLogin from '../headerLogin/HeaderLogin';
import './Login.css'; // Certifique-se de ter o CSS da tela de login

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false); // Estado para controlar o carregamento
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true); // Inicia o carregamento

        try {
            const response = await fetch('http://localhost:3000/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();

            if (response.ok) {
                localStorage.setItem('token', data.token);
                navigate('/menu');
            } else {
                setError(data.message);
            }
        } catch (err) {
            setError('Erro na conexão com o servidor.');
        } finally {
            setLoading(false); // Para o carregamento após a resposta
        }
    };

    return (
        <div>
            <HeaderLogin /> {/* Adicionando o Header */}

            {loading ? ( // Mostra a tela de carregamento enquanto a requisição está sendo processada
                <div className="loading-screen">
                    <div className="spinner"></div>
                </div>
            ) : (
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>Email:</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div>
                        <label>Senha:</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <button type="submit">Login</button>
                </form>
            )}
        </div>
    );
}

export default Login;
