import React, { useState } from 'react';
import './Menu.css'; // Importa o CSS
import { FaHome, FaBook, FaGraduationCap, FaCalendarAlt, FaComments, FaQuestionCircle, FaBars } from 'react-icons/fa';

const Menu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div>
      {/* Botão hambúrguer */}
      <button className="hamburger" onClick={toggleMenu}>
        <FaBars />
      </button>

      {/* Menu lateral */}
      <div className={`menu-container ${isMenuOpen ? 'open' : ''}`}>
        <ul className="menu">
          <li>
            <FaHome />
            <span>Home</span>
          </li>
          <li>
            <FaBook />
            <span>Catálogo</span>
          </li>
          <li>
            <FaGraduationCap />
            <span>Pós-Graduação</span>
          </li>
          <li>
            <FaCalendarAlt />
            <span>Eventos</span>
          </li>
          <li>
            <FaComments />
            <span>Validardores</span>
          </li>
        </ul>

        {/* Itens do menu inferior */}
        <ul className="menu-bottom">
          <li>
            <FaQuestionCircle />
            <span>Ajuda</span>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Menu;
