import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Menu.css';
import { FaHome, FaBook, FaQuestionCircle, FaBars, FaUserCircle } from 'react-icons/fa';

const Menu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
    // Recupera o nome de usuário do localStorage
    const storedUsername = localStorage.getItem('username');
    if (storedUsername && storedUsername !== 'undefined' && storedUsername !== '') {
      setUsername(storedUsername); // Define o nome do usuário
    } else {
      setUsername('Guest'); // Exibe 'Guest' se não houver usuário logado
    }
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className="menu">
      <button className="hamburger" onClick={toggleMenu}>
        <FaBars />
      </button>

      <div className={`menu-container ${isMenuOpen ? 'open' : ''}`}>
        
        {/* Exibe o nome do usuário ou 'Guest' se não houver usuário definido */}
        <div className="user-info">
          <FaUserCircle style={{ marginRight: '10px', fontSize: '24px' }} />
          <span>{username}</span> {/* Exibe o nome do usuário */}
        </div>

        <ul className="menu">
          <li>
            <FaHome />
            <Link to="/home">Home</Link>
          </li>
          <li>
            <FaBook />
            <Link to="/catalogo">Catálogo</Link>
          </li>
        </ul>

        <ul className="menu-bottom">
          <li>
            <FaQuestionCircle />
            <Link to="/ajuda">Ajuda</Link>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Menu;
