import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import HeaderLogin from '../headerLogin/HeaderLogin';
import './CreateUser.css'; // Asegure-se de que o caminho está correto

const CreateUser = () => {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        name: '' // Supondo que você queira também o nome do usuário
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
        if (error) setError('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const response = await fetch('http://localhost:3000/api/users', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            const data = await response.json();
            console.log('Resposta da API:', data);

            if (response.ok) {
                alert('Usuário criado com sucesso!');
                navigate('/login'); // Redireciona para a página de login ou outra página de sua escolha
            } else {
                throw new Error(data.message || 'Erro ao criar usuário.');
            }
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <HeaderLogin />
            {loading ? (
                <div className="loading-screen">
                    <div className="spinner"></div>
                </div>
            ) : (
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>Nome:</label>
                        <input
                            type="text"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div>
                        <label>Email:</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div>
                        <label>Senha:</label>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <button type="submit" disabled={loading}>
                        {loading ? 'Carregando...' : 'Criar Usuário'}
                    </button>
                </form>
            )}
        </div>
    );
};

export default CreateUser;
