import React, { useState } from 'react';
import './Menu.css'; // Importa o CSS
import { Link } from 'react-router-dom'; // Importa o Link do React Router
import { FaHome, FaBook, FaGraduationCap, FaCalendarAlt, FaComments, FaQuestionCircle, FaBars, FaBeer } from 'react-icons/fa';

const Menu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div>
      {/* Botão hambúrguer */}
      <button className="hamburger" onClick={toggleMenu}>
        <FaBars />
      </button>

      {/* Menu lateral */}
      <div className={`menu-container ${isMenuOpen ? 'open' : ''}`}>
        <ul className="menu">
          <li>
            <FaHome />
            <Link to="/">Home</Link>
          </li>
          <li>
            <FaBook />
            <Link to="/catalogo">Catálogo</Link>
          </li>
          <li>
            <FaGraduationCap />
            <Link to="/pos-graduacao">Pós-Graduação</Link>
          </li>
          <li>
            <FaCalendarAlt />
            <Link to="/eventos">Eventos</Link>
          </li>
          <li>
            <FaComments />
            <Link to="/validadores">Validadores</Link>
          </li>
          <li>
            <FaBeer />
            <Link to="/layouts">Layouts</Link>
          </li>
        </ul>

        {/* Itens do menu inferior */}
        <ul className="menu-bottom">
          <li>
            <FaQuestionCircle />
            <Link to="/ajuda">Ajuda</Link>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Menu;
