import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../../components/headerLogin/HeaderLogin'; // Importação correta do HeaderLogin
import '../../components/auth/Login'; // Importação correta do Login
import '../../components/menu/Menu'; // Importação correta do Menu
import '../home/Home'; // Importação correta do Home
import './Layouts.css'; // Este está correto conforme sua estrutura de diretório


function CreateUser() {
  const [formData, setFormData] = useState({ name: '', email: '', password: '' });

  const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
      e.preventDefault();
      try {
          const response = await fetch('http://localhost:3001/api/users', {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json',
              },
              body: JSON.stringify(formData),
          });
          if (response.ok) {
              alert('Usuário criado com sucesso!');
              setFormData({ name: '', email: '', password: '' }); // Limpar o formulário
          } else {
              const data = await response.json();
              alert(data.message);
          }
      } catch (error) {
          alert('Erro ao enviar dados!');
      }
  };

  return (
      <form onSubmit={handleSubmit}>
          <label>
              Nome:
              <input type="text" name="name" value={formData.name} onChange={handleChange} />
          </label>
          <label>
              Email:
              <input type="email" name="email" value={formData.email} onChange={handleChange} />
          </label>
          <label>
              Senha:
              <input type="password" name="password" value={formData.password} onChange={handleChange} />
          </label>
          <button type="submit">Criar Usuário</button>
      </form>
  );
}

export default CreateUser;
