import React, { useState, useEffect } from 'react';
import { createUser, fetchUsers, updateUser, deleteUser } from '../../services/userService'; // Adicionar updateUser
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { ClipLoader } from 'react-spinners';
import { FaTimes } from 'react-icons/fa';
import './CreateUser.css';

function CreateUserComponent() {
    const [user, setUser] = useState({
        nome: '',
        email: '',
        password: '',
        confirmPassword: '',
        permission_id: '2',
    });

    const [users, setUsers] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [isFormVisible, setIsFormVisible] = useState(false);
    const [selectedUser, setSelectedUser] = useState(null); // Estado para o usuário selecionado

    useEffect(() => {
        async function loadUsers() {
            setLoading(true);
            try {
                const usersData = await fetchUsers();
                setUsers(usersData);
            } catch (err) {
                setErrorMessage('Erro ao buscar usuários: ' + err.message);
            } finally {
                setLoading(false);
            }
        }
        loadUsers();
    }, []);

    // Preenche o formulário com os dados do usuário selecionado
    const handleUserClick = (user) => {
        setSelectedUser(user); // Define o usuário selecionado
        setUser({
            nome: user.nome,
            email: user.email,
            password: '',
            confirmPassword: '',
            permission_id: String(user.permission_id),
        });
        setIsFormVisible(true); // Exibe o formulário em modo de edição
    };

    // Função para atualizar ou criar usuário
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (user.password !== user.confirmPassword) {
            toast.error('As senhas não coincidem.');
            return;
        }

        setIsSubmitting(true);
        try {
            if (selectedUser) {
                // Atualiza o usuário existente
                await updateUser(selectedUser.id, user);
                toast.success('Usuário atualizado com sucesso!');
            } else {
                // Cria um novo usuário
                await createUser(user);
                toast.success('Usuário criado com sucesso!');
            }

            const usersData = await fetchUsers();
            setUsers(usersData);
            setSelectedUser(null); // Limpa o usuário selecionado após a edição
            setIsFormVisible(false);
        } catch (error) {
            const errMsg = error.message || 'Erro ao processar o usuário.';
            setErrorMessage(errMsg);
            toast.error(errMsg);
        } finally {
            setIsSubmitting(false);
        }
    };

    const handleDeleteUser = async (userId) => {
        try {
            await deleteUser(userId);
            toast.success('Usuário excluído com sucesso!');
            const updatedUsers = users.filter(user => user.id !== userId);
            setUsers(updatedUsers);
        } catch (error) {
            toast.error('Erro ao excluir usuário.');
        }
    };

    return (
        <div className="formContainer">
            {isSubmitting && (
                <div className="loading-overlay">
                    <ClipLoader color="#007bff" size={60} />
                </div>
            )}

            <button
                className="openFormButton"
                onClick={() => {
                    setIsFormVisible(true);
                    setSelectedUser(null); // Reseta o usuário selecionado ao criar novo
                }}
            >
                {isFormVisible ? 'Fechar Formulário' : 'Criar Usuário'}
            </button>

            {isFormVisible && (
                <div className="fullscreen-form">
                    <form onSubmit={handleSubmit} className="user-form">
                        <h2>{selectedUser ? 'Editar Usuário' : 'Criar Usuário'}</h2>
                        <FaTimes className="close-form-icon" onClick={() => setIsFormVisible(false)} />
                        <div className="inputField">
                            <label>Nome:</label>
                            <input type="text" name="nome" value={user.nome} onChange={(e) => setUser({ ...user, nome: e.target.value })} required />
                        </div>
                        <div className="inputField">
                            <label>Email:</label>
                            <input type="email" name="email" value={user.email} onChange={(e) => setUser({ ...user, email: e.target.value })} required />
                        </div>
                        <div className="inputField">
                            <label>Senha:</label>
                            <input type="password" name="password" value={user.password} onChange={(e) => setUser({ ...user, password: e.target.value })} required />
                        </div>
                        <div className="inputField">
                            <label>Confirmar Senha:</label>
                            <input type="password" name="confirmPassword" value={user.confirmPassword} onChange={(e) => setUser({ ...user, confirmPassword: e.target.value })} required />
                        </div>
                        <div className="inputField">
                            <label>Permissão:</label>
                            <select name="permission_id" value={user.permission_id} onChange={(e) => setUser({ ...user, permission_id: e.target.value })} required>
                                <option value={1}>Total</option>
                                <option value={2}>Limitada</option>
                            </select>
                        </div>
                        <button type="submit" className="submitButton" disabled={isSubmitting}>
                            {isSubmitting ? 'Enviando...' : selectedUser ? 'Atualizar Usuário' : 'Criar Usuário'}
                        </button>
                    </form>
                </div>
            )}

            <ToastContainer />
            {errorMessage && <div style={{ color: 'red' }}>{errorMessage}</div>}

            <h2>Lista de Usuários</h2>
            {loading ? (
                <ClipLoader color="#007bff" size={60} />
            ) : (
                <table className="user-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Nome</th>
                            <th>Email</th>
                            <th>Permissão</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user) => (
                            <tr key={user.id} onClick={() => handleUserClick(user)}> {/* Chama handleUserClick ao clicar */}
                                <td>{user.id}</td>
                                <td>{user.nome}</td>
                                <td>{user.email}</td>
                                <td>{parseInt(user.permission_id) === 1 ? 'Permissão total' : 'Permissão limitada'}</td>
                                <td>
                                    <button className="deleteButton" onClick={() => handleDeleteUser(user.id)}>
                                        Excluir
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default CreateUserComponent;
