import '../../components/headerLogin/HeaderLogin'; // Importação correta do HeaderLogin
import '../../components/auth/Login'; // Importação correta do Login
import '../../components/menu/Menu'; // Importação correta do Menu
import '../home/Home'; // Importação correta do Home
import React, { useState, useEffect } from 'react'; // Corrigi a importação de useEffect
import './CreateUser.css';
import { createUser, fetchUsers } from '../../services/userService'; // Certifique-se de que fetchUsers está implementado no userService
import { toast, ToastContainer } from 'react-toastify';  
import 'react-toastify/dist/ReactToastify.css';  

function CreateUserComponent() {
    const [user, setUser] = useState({
        nome: '',
        email: '',
        password: '',
        permission_id: '1',
    });
    const [users, setUsers] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');
    const [loading, setLoading] = useState(false); // Adicionando estado de loading
    const [isSubmitting, setIsSubmitting] = useState(false); // Estado para desabilitar botão durante a submissão

    useEffect(() => {
        async function loadUsers() {
            setLoading(true); // Começar o loading
            try {
                const usersData = await fetchUsers();
                setUsers(usersData);
            } catch (err) {
                setErrorMessage('Erro ao buscar usuários: ' + err.message);
            } finally {
                setLoading(false); // Finalizar o loading
            }
        }
        loadUsers();
    }, []);

    const handleChange = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true); // Desabilitar o botão
        try {
            await createUser(user);
            setUser({ nome: '', email: '', password: '', permission_id: '1' });
            toast.success('Usuário criado com sucesso!');
            const usersData = await fetchUsers();
            setUsers(usersData);
        } catch (error) {
            const errMsg = error.message || 'Erro ao criar usuário.';
            setErrorMessage(errMsg);
            toast.error(errMsg);
            console.error(error); // Exibe o erro no console para ajudar no debug
        } finally {
            setIsSubmitting(false); // Habilitar o botão novamente
        }
    };

    return (
        <div className="formContainer">
            <form onSubmit={handleSubmit} className="user-form">
                <h2>Criar Usuário</h2>
                <div className="inputField">
                    <label>Nome:</label>
                    <input type="text" name="nome" value={user.nome} onChange={handleChange} required />
                </div>
                <div className="inputField">
                    <label>Email:</label>
                    <input type="email" name="email" value={user.email} onChange={handleChange} required />
                </div>
                <div className="inputField">
                    <label>Senha:</label>
                    <input type="password" name="password" value={user.password} onChange={handleChange} required />
                </div>
                <button type="submit" className="submitButton" disabled={isSubmitting}>
                    {isSubmitting ? 'Enviando...' : 'Criar Usuário'}
                </button>
            </form>

            <ToastContainer />
            {errorMessage && <div style={{ color: 'red' }}>{errorMessage}</div>}

            <h2>Lista de Usuários</h2>
            {loading ? (
                <p>Carregando usuários...</p>
            ) : (
                <table className="user-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Nome</th>
                            <th>Email</th>
                            <th>Permissão</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user.id}>
                                <td>{user.id}</td>
                                <td>{user.nome}</td>
                                <td>{user.email}</td>
                                <td>{user.permission_id === '1' ? 'Total' : 'Limitada'}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default CreateUserComponent;
